public class While {
    public static void main(String args[]) {
        //Todo Auto-generated method stub
        System.out.println("Numeros del 1 al 100 (usando while): "); //imprimo en pantalla el mensaje
      int i=1;
      while(i<=100){
      System.out.println(i); //se ejecuta el proceso
      i++;}
    }
}